import { Container, Row, Col } from 'react-bootstrap';
import moment from 'moment';
import {MovieItem} from '../App/MovieItem'

// you can access the sample dataset with:
import movieData from '../../movies.json'
import './App.scss';
import { useState } from 'react';



const Modal = (closeModal, movie) => {
  const { poster_path, title, overview } = movie
  return (
    <div>
      <div className='close-btn' onClick={() => closeModal()}>x</div>

      <div className='details'>
        <div className='details__left'><img src={`https://image.tmdb.org/t/p/original/${poster_path}`} /></div>
        <div className='details__right'>
          <h1>{title}</h1>
          <p>{overview}</p>
        </div>
      </div>


    </div>
  )
}



function App() {
  const [showModal, setShowModal] = useState(false)
  const [selectedMovie, setSelectedMovie] = useState(null)

  const openModal = (movie) => {
    setSelectedMovie(movie)
    setShowModal(true)
  }

  const closeModal = () => {
    setShowModal(false)
  }

  return (
    <div className="App">
      <div className='movie_contaner'>
        <div className='movie_grid'>
          {
            movieData && movieData.map((movie) => {
              return (
                <MovieItem movie={movie} openModal={openModal}/>
              )
            })
          }
        </div>
      </div>
      {
        showModal && <div className='modal_container'>
          <div className='info'>
            {
              selectedMovie && <Modal closeModal={closeModal} selectedMovie={selectedMovie} />
            }
          </div>
        </div>
      }
    </div>
  );
}

export default App;
