import { Container, Row, Col } from 'react-bootstrap';

// you can access the sample dataset with:
import movieData from '../../movies.json'
import './App.scss'; 

function App() {
  return (
    <div className="App">
      <Container>
        {
          movieData && movieData.map((movie) => {
            const {
              backdrop_path,
              genre_ids,
              id,
              original_title,
              original_language,
              overview,
              popularity,
              release_date,
              poster_path,
              title,
              video,
              vote_average,
              vote_count
            } = movie
            return (
              <div key={id} className='movie'>
                {/* <img src={`https://image.tmdb.org/t/p/original/${poster_path}`}/> */}
                {title}
                Released in {release_date}
                More info {overview}
              </div>
            )
          })
        }
        {/* <Row>
          <Col>Happy Coding!</Col>
        </Row> */}
      </Container>
    </div>
  );
}

export default App;
