import { Container, Row, Col } from 'react-bootstrap';
import moment from 'moment';

// you can access the sample dataset with:
import movieData from '../../movies.json'
import './App.scss'; 
import { useState } from 'react';




function App() {
  const [showModal,setShowModal] = useState(false)

  const openModal = () => {
    setShowModal(true)

  }

  const closeModal = () => {
    setShowModal(false)
  }

  return (      
    <div className="App">
      {/* <Container> */}

      <div className='movie_contaner'>
      <div className='movie_grid'>
        {
          movieData && movieData.map((movie) => {
            const {
              // backdrop_path,
              // genre_ids,
              id,
              // original_title,
              // original_language,
              // overview,
              // popularity,
              release_date,
              poster_path,
              title,
              // video,
              vote_average,
              // vote_count
            } = movie
            return (
              <div key={id} className='movie' onClick={() => openModal()}>
                {/* <img src={`https://image.tmdb.org/t/p/original/${poster_path}`}/> */}
                {/* <div className='movie__title'>{title}</div>
                <div className='movie__date'>{moment(release_date,"YYYY-MM-DD").format("LL")}</div>
                <div className='movie__rating'>{vote_average}</div> */}
              </div>
            )
          })
        }
        </div>

      </div>
      {
        showModal && <div className='movie_modal'>
          <button onClick={() => closeModal()}>close</button>

        </div>
      }


        {/* <Row>
          <Col>Happy Coding!</Col>
        </Row> */}
      {/* </Container> */}
    </div>
  );
}

export default App;
